package com.garenhart.music.makingmusicwithjava;import jm.JMC;import jm.audio.Instrument;import jm.music.data.Note;import jm.music.data.Part;import jm.music.data.Phrase;import jm.music.data.Score;import jm.util.View;import static com.garenhart.music.makingmusicwithjava.HelperIO.viewAudio;import static com.garenhart.music.makingmusicwithjava.HelperIO.writeAudio;public final class PitchInHertz implements JMC{    public static void main(String[] args){        Score score = new Score("Frequency Score");        Part part = new Part("A Part", 0);        for(int i=0; i<20; i++) {            Note note = new Note(Math.random() * 500 + 1000, 4.0);            note.setPan(Math.random());            Phrase phrase = new Phrase(note, Math.random() * 10);            part.addPhrase(phrase);        }        score.addPart(part);        View.print(score);        Instrument inst = new SineInst(22000);        writeAudio(score, "PitchInHertz.au", inst);        viewAudio("PitchInHertz.au");            }}							